mapper.select:
select collect_data, date_format(t.sta_time, '%Y-%m-%d') insert_time from t_collect_statis_week t where t.equip_id in
#{item, jdbcType=INTEGER}
and t.collect_type = #{compareType} and t.year = date_format(now(), '%Y') and t.week_year = WEEK ( date_add( now( ), INTERVAL 6 DAY ), 2 )

select collect_data,date_format(t.insert_time, '%Y-%m-%d') insert_time from t_collect_statis_month t where t.equip_id in
#{item, jdbcType=INTEGER}
and t.collect_type = #{compareType} and t.month = DATE_FORMAT( NOW( ), '%m' ) and t.year = date_format(now(), '%Y')

select collect_data ,date_format(t.insert_time, '%Y-%m') insert_time
 from   t_collect_statis_year t
  where    t.equip_id in

     #{item, jdbcType=INTEGER}

 and t.collect_type = #{compareType}
 and t.year = date_format(now(), '%Y')

select sum(collect_data)  data  from   t_collect_statis_day t where 1=1
 and t.collect_type = #{collectType}
 and   t.insert_time >= #{startDate} and t.insert_time <= #{endDate}

select   date_format(t.insert_time, '%Y-%m-%d %H:%i') dateStr, t.collec_value data,t.equip_id  equipId
from  t_press t ,t_equip_color tec ,t_equipment te
where 1=1 and t.equip_id = tec.id and tec.equip_id = te.id

    AND te.id = #{map.id}

and   t.insert_time >= #{map.startDate} and t.insert_time <= #{map.endDate}

select te.id equipId, te.`name` NAME, truncate (1.732 * 6000 * t.collec_value *0.85/1000/60,4) powerRate, date_format( t.insert_time, '%Y-%m-%d %H:%i' ) dateStr from t_power t,t_equip_color tec ,t_equipment te where t.equip_id = tec.id and tec.equip_id = te.id and t.equip_id != '1' and te.id in (53,54,55,56,57,58,59,60,61)
AND te.id = #{map.id}
and t.insert_time >= #{map.startDate} and t.insert_time <= #{map.endDate}

select sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) powerData, sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) data, date_format(t.insert_time, '%Y-%m-%d %H ') insert_time,equip_id equipId from t_collect_statis_day t where t.equip_id in
#{equipId}
and t.collect_type in (2,3) and t.insert_time >= #{startDate} and t.insert_time <= #{endDate} group by date_format(t.insert_time, '%Y-%m-%d %H'),equip_id

select sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) powerData, sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) data, date_format(t.insert_time, '%Y-%m-%d') insert_time,equip_id equipId from t_collect_statis_day t where t.equip_id in
#{equipId}
and t.collect_type in (2,3) and t.insert_time >= #{startDate} and t.insert_time <= #{endDate} group by date_format(t.insert_time, '%Y-%m-%d'),equip_id

select sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) powerData, sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) data, date_format(t.insert_time, '%Y-%m-%d') insert_time,equip_id equipId from t_collect_statis_day t where t.equip_id in
#{equipId}
and t.collect_type in (2,3) and t.insert_time >= #{startDate} and t.insert_time <= #{endDate} group by date_format(t.insert_time, '%Y-%m-%d'),equip_id

select sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) powerData, sum(if(collect_type = 2,collect_data,0))/count(if(collect_type = 2,1,0)) data, group by date_format(t.insert_time, '%Y-%m') date_format(t.insert_time, '%Y-%m-%d') insert_time,equip_id equipId from t_collect_statis_day t where t.equip_id in
#{equipId}
and t.collect_type in (2,3) and t.insert_time >= #{startDate} and t.insert_time <= #{endDate} group by date_format(t.insert_time, '%Y-%m'),equip_id
